import React from 'react';
import cookImage from '../../assets/cook.png';
type StepOneProps = {
  handleNext: () => void;
};
const StepOne: React.FC<StepOneProps> = ({ handleNext }) => {
  return (
    <>
      <div className='flex flex-row gap-2 '>
        <img src={cookImage} className='w-[7.8rem] h-[7.8rem]' />
        <div className='flex flex-col -ml-[0.9rem] gap-[0.7rem]'>
          <div className='-ml-[0.9rem]'>
            <svg
              xmlns='http://www.w3.org/2000/svg'
              width='24'
              height='24'
              viewBox='0 0 24 24'
              fill='none'
            >
              <path
                d='M16.25 15.5C14.413 14.706 13.75 14 13.75 12C13.75 10 15 8.75 17 8.75C19 8.75 20.25 10 20.25 12C20.25 15.5 19.25 16.75 15.25 19.25C16.25 17.5 16.25 17 16.25 15.5ZM6.25 15.5C4.413 14.706 3.75 14 3.75 12C3.75 10 5 8.75 7 8.75C9 8.75 10.25 10 10.25 12C10.25 15.5 9.25 16.75 5.25 19.25C6.25 17.5 6.25 17 6.25 15.5Z'
                stroke='white'
                stroke-width='1.5'
                stroke-linecap='round'
                stroke-linejoin='round'
              />
            </svg>
          </div>
          <h1
            className='text-white text-xl sm:text-2xl font-bold mb-7 sm:mb-8 text-left leading-[1.25] '
            style={{
              fontFamily:
                'Poppins, -apple-system, Roboto, Helvetica, sans-serif',
            }}
          >
            Hi there! What type of catering you need?
          </h1>
        </div>
      </div>
      <div className='grid grid-cols-3 gap-4 px-[2.3rem] mt-[0.8rem]'>
        <CategoryCard
          icon={<CorporateIcon />}
          label='Corporate'
          handleNext={handleNext}
        />
        <CategoryCard
          icon={<WeddingIcon />}
          label='Wedding'
          handleNext={handleNext}
        />
        <CategoryCard
          icon={<PrivatePartiesIcon />}
          label='Private Parties'
          handleNext={handleNext}
        />
        <CategoryCard
          icon={<OutdoorIcon />}
          label='Outdoor'
          handleNext={handleNext}
        />
        <CategoryCard
          icon={<FundRaisingIcon />}
          label='Fund Raising'
          handleNext={handleNext}
        />
        <CategoryCard
          icon={<OthersIcon />}
          label='Others'
          handleNext={handleNext}
        />
      </div>
      <div className='flex items-center justify-center gap-2 text-white text-xs mt-[2rem]'>
        <span
          className='text-white/80'
          style={{
            fontFamily: 'Poppins, -apple-system, Roboto, Helvetica, sans-serif',
          }}
        >
          Powered By
        </span>
        <div className='flex items-center gap-1.5'>
          <svg
            width='12'
            height='11'
            viewBox='0 0 12 11'
            fill='none'
            xmlns='http://www.w3.org/2000/svg'
          >
            <path
              d='M8.29815 7.66709C6.87625 9.39672 1.95057 10.6412 0.231751 7.12634C1.42038 10.2751 6.89459 11.6455 10.9309 8.78093L8.29815 7.66709Z'
              fill='#DA291C'
            />
            <path
              d='M11.0508 1.65652C9.37343 -0.00657711 4.49983 -0.0965629 2.95482 3.05139C3.71021 2.13114 6.31189 1.16425 8.41809 3.18657L11.0508 1.65652Z'
              fill='#3FC366'
            />
            <path
              d='M7.61107 0.199333C5.61152 0.228501 1.51957 1.41224 2.4527 6.02552C2.68445 6.85596 3.61196 8.62127 5.74482 8.78093C3.97692 9.26568 0.714094 9.21804 0.10079 5.85364C-0.0984313 4.76077 0.0570256 3.6008 0.631585 2.65004C1.60142 1.0452 3.71414 -0.57459 7.61107 0.199333Z'
              fill='#FFC72C'
            />
          </svg>
          <span
            className='font-normal'
            style={{
              fontFamily:
                'Montserrat, -apple-system, Roboto, Helvetica, sans-serif',
            }}
          >
            CATERSWEET
          </span>
          <span
            className='font-bold'
            style={{
              fontFamily:
                'Poppins, -apple-system, Roboto, Helvetica, sans-serif',
            }}
          >
            FORMS
          </span>
        </div>
      </div>
    </>
  );
};
export default StepOne;

function CategoryCard({
  icon,
  label,
  handleNext,
}: {
  icon: React.ReactNode;
  label: string;
  handleNext: () => void;
}) {
  return (
    <>
      <div className='group flex flex-col items-center justify-between gap-2 '>
        <div
          className='w-[5.79094rem] min-h-[7.25813rem] flex items-center justify-center flex-shrink-0 bg-[rgba(62,62,62,0.5)] border border-[#C2C2C2] rounded-[10px] hover:bg-[rgba(62,62,62,0.7)] transition-all duration-200 hover:scale-[1.02] hover:border-white/50 backdrop-blur-sm active:scale-95'
          onClick={handleNext}
        >
          {icon}
        </div>
        <span
          className='text-white text-sm font-semibold text-center'
          style={{
            fontFamily: 'Poppins, -apple-system, Roboto, Helvetica, sans-serif',
            lineHeight: '96%',
          }}
        >
          {label}
        </span>
      </div>
    </>
  );
}

